pragma solidity ^0.8.20;

import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {OwnerIsCreator} from "@chainlink/contracts-ccip/src/v0.8/shared/access/OwnerIsCreator.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {CCIPReceiver} from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import {IERC20} from "@chainlink/contracts-ccip/src/v0.8/vendor/openzeppelin-solidity/v4.8.3/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@chainlink/contracts-ccip/src/v0.8/vendor/openzeppelin-solidity/v4.8.3/contracts/token/ERC20/utils/SafeERC20.sol";

    using SafeERC20 for IERC20;

contract Acknowledger is CCIPReceiver, OwnerIsCreator {

    error NotEnoughBalance(uint256 currentBalance, uint256 calculatedFees);
    error NothingToWithdraw();
    error DestinationChainNotAllowlisted(uint64 destinationChainSelector);
    error InvalidReceiverAddress();
    error SourceChainNotAllowlisted(uint64 sourceChainSelector);
    error SenderNotAllowlisted(address sender);

    string private s_lastReceivedText;


    mapping(uint64 => bool) public allowlistedDestinationChains;
    mapping(uint64 => bool) public allowlistedSourceChains;
    mapping(address => bool) public allowlistedSenders;

    event AcknowledgmentSent(
        bytes32 indexed messageId,
        uint64 indexed destinationChainSelector,
        address indexed receiver,
        bytes32 data,
        address feeToken,
        uint256 fees
    );

    IERC20 private s_linkToken;
    constructor(address _router, address _link) CCIPReceiver(_router) {
        s_linkToken = IERC20(_link);
    }

    modifier onlyAllowlistedDestinationChain(uint64 _destinationChainSelector) {
        if (!allowlistedDestinationChains[_destinationChainSelector])
            revert DestinationChainNotAllowlisted(_destinationChainSelector);
        _;
    }

    modifier onlyAllowlisted(uint64 _sourceChainSelector, address _sender) {
        if (!allowlistedSourceChains[_sourceChainSelector])
            revert SourceChainNotAllowlisted(_sourceChainSelector);
        if (!allowlistedSenders[_sender]) revert SenderNotAllowlisted(_sender);
        _;
    }

    function allowlistDestinationChain(
        uint64 _destinationChainSelector,
        bool allowed
    ) external onlyOwner {
        allowlistedDestinationChains[_destinationChainSelector] = allowed;
    }

    function allowlistSourceChain(
        uint64 _sourceChainSelector,
        bool allowed
    ) external onlyOwner {
        allowlistedSourceChains[_sourceChainSelector] = allowed;
    }

    function allowlistSender(address _sender, bool allowed) external onlyOwner {
        allowlistedSenders[_sender] = allowed;
    }

    function _acknowledgePayLINK(bytes32 _messageIdToAcknowledge, address _messageTrackerAddress,
        uint64 _messageTrackerChainSelector) private {
        if (_messageTrackerAddress == address(0))
            revert InvalidReceiverAddress();
        Client.EVM2AnyMessage memory acknowledgment = Client.EVM2AnyMessage({
            receiver: abi.encode(_messageTrackerAddress),
            data: abi.encode(_messageIdToAcknowledge),
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: Client._argsToBytes(

                Client.EVMExtraArgsV1({gasLimit: 200_000})
            ),

            feeToken: address(s_linkToken)
        });

        IRouterClient router = IRouterClient(this.getRouter());

        uint256 fees = router.getFee(
            _messageTrackerChainSelector,
            acknowledgment
        );

        if (fees > s_linkToken.balanceOf(address(this))) {
            revert NotEnoughBalance(s_linkToken.balanceOf(address(this)), fees);
        }

        s_linkToken.approve(address(router), fees);

        bytes32 messageId = router.ccipSend(
            _messageTrackerChainSelector,
            acknowledgment
        );

        emit AcknowledgmentSent(
            messageId,
            _messageTrackerChainSelector,
            _messageTrackerAddress,
            _messageIdToAcknowledge,
            address(s_linkToken),
            fees
        );
    }

    function _ccipReceive(Client.Any2EVMMessage memory any2EvmMessage) internal override
    onlyAllowlisted(any2EvmMessage.sourceChainSelector, abi.decode(any2EvmMessage.sender, (address))) {
        bytes32 messageIdToAcknowledge = any2EvmMessage.messageId;
        address messageTrackerAddress = abi.decode(
            any2EvmMessage.sender,
            (address)
        );
        uint64 messageTrackerChainSelector = any2EvmMessage.sourceChainSelector;
        s_lastReceivedText = abi.decode(any2EvmMessage.data, (string));

        _acknowledgePayLINK(
            messageIdToAcknowledge,
            messageTrackerAddress,
            messageTrackerChainSelector
        );
    }

    function getLastReceivedMessage() external view returns (string memory text) {
        return (s_lastReceivedText);
    }

    function withdrawToken(address _beneficiary, address _token) public onlyOwner {
        uint256 amount = IERC20(_token).balanceOf(address(this));
        if (amount == 0) revert NothingToWithdraw();
        IERC20(_token).safeTransfer(_beneficiary, amount);
    }
}

